import useState from "react";

export const DarkMode = () => {
    const [theme, setTheme] =useState('darkmode');

    const toggleTheme = () =>{
        theme === 'dark' ? setTheme('light'): setTheme('dark');
    }
    return [theme, toggleTheme]
}
