import React, { Component } from 'react';



class Entries extends Component {
    render() {
        const entries = [
            {
                title: "My first entry",
                body: "This is my journal entry"
            },
            {
                title: "My second entry",
                body: "This is my journal entry"
            },
            {
                title: "My third entry",
                body: "This is my journal entry"
            }
        ]
        return (
            <div>
                <h2>My Journal App</h2>
                <Entries eneryData={entries}/>
            </div>
        )
    }
}

