import React, { useEffect, useState } from "react";
import ListButton from "../component/gotolist";
import axios from 'axios';
import ListItem from '../component/ListItem';


const happys = ["0", "1", "2", "3", "4", "5"];
const angrys = ["0", "1", "2", "3", "4", "5"];
const sads = ["0", "1", "2", "3", "4", "5"];
const joys = ["0", "1", "2", "3", "4", "5"];


export default function WritePage(){
    const diarysectionstyle={
        width: '700px',
        height: '350px',
        maxlength: '20'
    };

    const [happy, setHappy] = useState("0");
    const [angry, setAngry] = useState("0");
    const [sad, setSad] = useState("0");
    const [joy, setJoy] = useState("0");
    const [input, setInput] = useState([]);
    const [addInput,setAddInput] =useState("");
    
    useEffect(() => {
        // 목록 조회 요청 전송
        axios.get(`/api/list`)
        // 응답이 돌아오면 응답 내용으로 목록을 변경
        .then(response => {
          setInput(response.data);
        });
      }, []);

    const onDeleteClick = (item) => {
        axios.delete(`api/todo/${item._id}`)
        .then(()=>axios.get(`api/todo`))
        .catch((err)=>{
          if(err.response.code === 403){
            alert("로그인을 해주세요")
          } else alert(`오류! ${err}`);
        })
        .then(response => {
          setInput(response.data);
        })
      };
    
      const onSaveClick = () => {
        // 추가 요청 전송
        axios.post(`/api/list`, {
          name: addInput
        })
        // 완료 후 목록 조회 요청 전송
        .then(() => axios.get(`/api/list`))
        // 응답이 돌아오면 응답 내용으로 목록을 변경
        .then(response => {
          setInput(response.data);
          // 입력란을 비우기
          setAddInput("");
        });
      };
    
      const listEls = input.map(v => (
        <ListItem
          key={v.id}
          name={v.name}
          onDeleteClick={() => onDeleteClick(v)}
        />
      ));
    return (
        <div>
            <header className="intro">
                <h1>오늘 하루는 어땠나요?</h1>
                <h2>각각의 감정을 느낀 정도를 숫자를 통해 표현해보세요.</h2>
            </header>
            <body className="choose emotion">
                <div className="happy">
                    <label> 희 </label>
                    {happys.map(f => (
                    <>
                        <input
                            type="radio"
                            name="happy"
                            value={f}
                            checked={happy===f}
                            onChange={e=>setHappy(e.currentTarget.value)}
                        />{" "}
                        {f}
                    </>    
                    ))}
                </div>
                <br />
                <div className="angry">
                    <label> 노 </label>
                    {angrys.map(f => (
                    <>
                        <input
                            type="radio"
                            name="angry"
                            value={f}
                            checked={angry===f}
                            onChange={e=>setAngry(e.currentTarget.value)}
                        />{" "}
                        {f}
                    </>    
                    ))}
                </div>
                <br />
                <div className="sad">
                    <label> 애 </label>
                    {sads.map(f => (
                    <>
                        <input
                            type="radio"
                            name="sad"
                            value={f}
                            checked={sad===f}
                            onChange={e=>setSad(e.currentTarget.value)}
                        />{" "}
                        {f}
                    </>    
                    ))}
                </div>
                <br />
                <div className="joy">
                    <label> 락 </label>
                    {joys.map(f => (
                    <>
                        <input
                            type="radio"
                            name="joy"
                            value={f}
                            checked={joy===f}
                            onChange={e=>setJoy(e.currentTarget.value)}
                        />{" "}
                        {f}
                    </>    
                    ))}
                </div>
                <br />
                <textarea
                    value={addInput}
                    style={diarysectionstyle}
                    onChange={v=>setAddInput(v.target.value)}
                    required/>
                <br />
                <button onClick={()=>onSaveClick()}>저장</button>
            </body>
            <ListButton />
        </div>
    );
}
