import { createGlobalStyle } from "styled-components";

const GlobalStyle= createGlobalStyle`
    *{
        padding: 0;
        margin: 0;
        list-style: none;
        box-sizing: border-box;
    }
    :root{
        --bg: #f2f6ff;
        --bg-body: #fff;
        --bg-menu: #F56692;
        --text1: #BDBDBD;
        --text2: #828282;
        --border: #FFC700;
        --btn-color: #F2F6FF;
        --circle-color: red;
    }
    .light{
        --bg-header: #f2f6ff;
        --bg-body: #fff;
        --bg-menu: #F56692;
        --text1: #BDBDBD;
        --text2: #828282;
        --border: #FFC700;
        --btn-color: #F2F6FF;
        --circle-color: red;
    }
    .dark{
        --bg-header: #1A1A40;
        --bg-body: #fff;
        --bg-menu: #F56692;
        --text1: #BDBDBD;
        --text2: #828282;
        --border: #FFC700;
        --btn-color: #F2F6FF;
        --circle-color: red;
    }
    .green{
        --bg-header: green;
        --bg-body: #fff;
        --bg-menu: #F56692;
        --text1: #BDBDBD;
        --text2: #828282;
        --border: #FFC700;
        --btn-color: #F2F6FF;
        --circle-color: red;
    }
    .yellow{
        --bg-header: yellow;
        --bg-body: #fff;
        --bg-menu: #F56692;
        --text1: red;
        --text2: white;
        --border: #FFC700;
        --btn-color: #F2F6FF;
        --circle-color: red;
    }
    body{
        background-color: var(--bg-body);
    }
    

`;

export default GlobalStyle;
