import { createGlobalStyle } from "styled-components";

const GlobalStyle= createGlobalStyle`
    *{
        padding: 0;
        margin: 0;
        list-style: none;
        box-sizing: border-box;
    }
    :root{
        --bg: #E9E5D6;
        --main: #464E2E;
        --sub1: #ACB992;
        --sub2: #ACB992;
        --text: #362706;
    }
    .light{
        --bg: #E9E5D6;
        --main: #BDBDBD;
        --sub1: #828282;
        --sub2: #FFC700;
        --text: #F2F6FF;
    }
    .cosmic{
        --bg: #E9E5D6;
        --main: #BDBDBD;
        --sub1: #828282;
        --sub2: #FFC700;
        --text: #F2F6FF;
    }
    .cottoncandy{
        --bg: #E9E5D6;
        --main: #BDBDBD;
        --sub1: #828282;
        --sub2: #FFC700;
        --text: #F2F6FF;
    }
    .chocotree{
        --bg: #E9E5D6;
        --main: #BDBDBD;
        --sub1: #828282;
        --sub2: #FFC700;
        --text: #F2F6FF;
    }
    .ocean{
        --bg: #E9E5D6;
        --main: #464E2E;
        --sub1: #ACB992;
        --sub2: #ACB992;
        --text: #362706;
    }
    body{
        background-color: var(--bg);
    }
`;

export default GlobalStyle;
