import { createGlobalStyle } from "styled-components";

const GlobalStyle= createGlobalStyle`
    *{
        padding: 0;
        margin: 0;
        list-style: none;
        box-sizing: border-box;
    }
    :root{
        --bg: #E9E5D6;
        --main: #464E2E;
        --sub1: #ACB992;
        --sub2: #ACB992;
        --text: #362706;
    }
    .light{
        --bg: #FFF8F3;
        --main: #A3E4DB;
        --sub1: #FED1EF;
        --sub2: #FFAFAF;
        --text: #1C6DD0;
    }
    .cosmic{
        --bg: #1A1A40;
        --main: #EF2F88;
        --sub1: #F9D371;
        --sub2: #F47340;
        --text: #FFF8F3;
    }
    .cottoncandy{
        --bg: #FFEBCC;
        --main: #FFAFAF;
        --sub1: RED;
        --sub2: #FF9999;
        --text: #744500;
    }
    .chocotree{
        --bg: #E9E5D6;
        --main: #464E2E;
        --sub1: #ACB992;
        --sub2: #ACB992;
        --text: #362706;
    }
    .ocean{
        --bg: #FFF8F3;
        --main: #74D6C8;
        --sub1: #96BAFF;
        --sub2: #3D56B2;
        --text: #10207E;
    }
    body{
        background-color: var(--bg);
    }
`;

export default GlobalStyle;
