import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle` 
    *{
        padding: 0;
        margin: 0;
    }
    :root{
        -bg-header: #f2f6ff;
        --btn-color: #E9E5D6;
    }
    .light{
        -bg-header: #FFF8F3;
        --btn-color: #FED1EF;
    }
    .dark{
        -bg-header: #f2f6ff;
        --btn-color: #bdbdbd;
    }
    .cosmic{
        -bg-header: #1A1A40;
        --btn-color: #8843F2;
    }
    body{
        background-color: var(--bg-header);
    }
`;

export default GlobalStyle;
