import styled from "styled-components";

function Menu({setTheme}) {
    
    return(
        <MenuStyled>
            <div className="buttons">
                <div className="button" onClick={()=>setTheme('light')}>
                    <div className="circle light"/>
                </div>
                <div className="button" onClick={()=>setTheme('cosmic')}>
                    <div className="circle cosmic"/>
                </div>
                <div className="button" onClick={()=>setTheme('cottoncandy')}>
                    <div className="circle cottoncandy"/>
                </div>
                <div className="button" onClick={()=>setTheme('chocotree')}>
                    <div className="circle chocotree"/>
                </div>
                <div className="button" onClick={()=>setTheme('ocean')}>
                    <div className="circle ocean"/>
                </div>

            </div>
        </MenuStyled>
    )
}

const MenuStyled = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;

    .buttons{
        background-color: var(--bg);
        padding: 10px 2rem;
        display: flex;
        align-items: center;
        background-color: var(--bg);
        .button{
            display: flex;
            align-items: center;
            cursor: pointer;
            &:not(:last-child){
                margin-right: 2rem;
            }
            .circle{
                width: 1.5rem;
                height: 1.5rem;
                border-radius: 30%;
                background-color: var(--theme-color);
                border: 2px solid var(--border);
                margin-right: auto.3rem;
                display: flex;
                align-items: center;
            }
        }
    }
    .texts{
        .smalltext{
            font-size: 20px;
            color: var(--text1);
        }
        .bigtext{
            font-size: 40px;
            color: var(--text2);
        }
    }
`;

export default Menu;
